import { createContext, useEffect, useState } from 'react';
import { onAuthStateChanged } from 'firebase/auth'; // Import onAuthStateChanged from firebase/auth
import { getAuth } from 'firebase/auth'; // Import getAuth to get the auth instance
import AuthContext from './AuthContext'; // Import the context you created

const auth = getAuth(); // Initialize the auth instance

export const AuthContextProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            setCurrentUser(user);
        });

        return () => unsubscribe();
    }, []);

    return (
        <AuthContext.Provider value={{ currentUser }}>
            {children}
        </AuthContext.Provider>
    );
};
