  return (
    <div>AuthContext</div>
  )
}

export default AuthContext=createContext()
export const AuthContextProvider=({children})=>{
    const[currentUser,setCurrentUser]=useState({})

    useEffect(()=>{
        onAuthStateChanged(auth,(user)=>{
            setCurrentUser(user);
            console.log(user);
        })
    },[])
    <AuthContext.Provider
}
